function user_status(id,status){
	BootstrapDialog.show({
	    title: 'User Status',
	    message: 'Are you sure, do you want to change user status?',
	    buttons: [{ id: 'btn-ok', icon: 'glyphicon glyphicon-check', label: 'Yes',
	        cssClass: 'btn-primary',  autospin: false,
	        action: function(dialogRef){    
	        	$.ajax({type: 'POST', url: ""+SITEURL+"lab/labs/up_user_st", data: {  id: id,status:status },
	                success: function(data) { $("#ajax_req").html(data); dialogRef.close();}, error: function(comment) {  dialogRef.close(); }});
	        } },
	    { id: 'btn-ok', icon: 'glyphicon glyphicon-remove', label: 'Cancel', cssClass: 'btn-primary', autospin: false, action: function(dialogRef){ dialogRef.close(); } }
	    ] });
}


